mapper.select:
select a.order_id orderId, a.amount amount, b.exam_signup_id examSignupId, b.exam_course examCourse, b.exam_type examType, b.price price, b.exam_version_json examVersionJson, b.exam_signup_status examSignupStatus, b.exam_start_time examStartTime, b.exam_end_time examEndTime, b.exam_detail_address examDetailAddress from ( select * from tbl_order a1
a1.del_flag = #{DEL_FLAG_NORMAL} and a1.acca_user_id = #{accaUserId} and a1.order_status not in (${sqlMap.idStr})
and a1.order_status = #{orderStatus}
and a1.exam_time_str = #{examTimeStr}
) a left join tbl_exam_signup b on a.order_id = b.order_id
order by a.order_id desc,b.exam_signup_id desc

select a.order_id orderId,a.check_reason checkReason, e.file_relative_url cardImgUrl,f.file_relative_url payImgUrl, a.register_name registerName,a.register_card_number registerCardNumber,a.amount amount,a.org org, a.phone phone,a.register_email registerEmail,a.acca_register_name accaRegisterName, a.register_phone registerPhone,a.acca_register_password accaRegisterPassword, b.exam_signup_id examSignupId,b.exam_course examCourse,b.exam_type examType,b.price price,b.exam_version_json examVersionJson,b.exam_id examId, b.exam_signup_status examSignupStatus,b.exam_start_time examStartTime,b.exam_end_time examEndTime,b.exam_detail_address examDetailAddress from tbl_order a left join tbl_attachment_file e on e.file_id = a.register_white_color_img_id left join tbl_attachment_file f on f.file_id = a.order_pay_img_id left join tbl_exam_signup b on a.order_id = b.order_id
a.del_flag = #{DEL_FLAG_NORMAL} and a.order_id = #{orderId}
order by a.order_id desc,b.exam_signup_id desc
