mapper.select:
select q.id as id, q.title as title, q.catergory_id as catergoryId, q.hot as hot, q.publisher as publisher, q.publish_time as publishTime, q.modifier as modifier, q.modify_time as modifyTime, q.answer as answer, c.name as category, p.name as publisherName, m.name as modifierName, praise.praise_num as praised, praise2.praise_num as disPraised from tbl_question q left join tbl_question_category c on q.catergory_id = c.id left join sys_user p on q.publisher = p.id left join sys_user m on q.modifier = m.id left join (select question_id, count(*) as praise_num from tbl_question_praise where praise_flag = 0 group by question_id ) praise on praise.question_id = q.id left join (select question_id, count(*) as praise_num from tbl_question_praise where praise_flag != 0 group by question_id ) praise2 on praise2.question_id = q.id
q.del_flag = "0"
AND q.title LIKE concat('%',#{title},'%')
AND q.publish_time > #{start}
AND #{end} > q.publish_time
AND q.hot = #{hot}
AND q.catergory_id = #{catergoryId}
