mapper.select:
SELECT

FROM tbl_invite a

WHERE a.id = #{id}

SELECT
FROM tbl_invite a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.inviter_phone LIKE '%'||#{inviterPhone}||'%' '%'+#{inviterPhone}+'%' concat('%',#{inviterPhone},'%')
AND a.invite_time > #{inviteStart}
AND #{inviteEnd} > a.invite_time
AND a.invite_time > date_sub(now(), interval 3 day) AND NOW() > a.invite_time
AND a.success_time is not null
AND date_sub(now(), interval 3 day) > a.invite_time
ORDER BY ${page.orderBy}

SELECT

FROM tbl_invite a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_invite SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
