mapper.select:
SELECT

FROM tbl_invite a

WHERE a.id = #{id}

SELECT

FROM tbl_invite a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.inviter_phone = ${inviterPhone}




        ORDER BY ${page.orderBy}

SELECT
FROM tbl_invite a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.inviter_phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.invite_time > ${start}
AND ${end} > a.invite_time

SELECT inviter_phone as phone, count(distinct (invitee_phone)) as successCount FROM tbl_invite a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.invite_time > ${start}
AND ${end} > a.invite_time
AND a.success_time IS NOT NULL
GROUP BY a.inviter_phone

SELECT

FROM tbl_invite a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_invite SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
