mapper.select:
SELECT a.acca_user_id AS "accaUserId" FROM tbl_acca_user a LEFT JOIN tbl_exam_signup es ON a.acca_user_id = es.acca_user_id AND es.exam_signup_status=30 AND es.exam_end_time > #{date} LEFT JOIN tbl_exam_course ec ON es.exam_course_id = ec.exam_course_id
a.del_flag = 0
AND es.exam_course_id=#{examCourseId}
AND a.identity_type=#{identityType}
AND a.type=#{type}
AND a.phone LIKE '%'||#{phone}||'%' '%'+#{phone}+'%' concat('%',#{phone},'%')
AND a.province_name LIKE '%'||#{provinceName}||'%' '%'+#{provinceName}+'%' concat('%',#{provinceName},'%')
AND a.city_name LIKE '%'||#{cityName}||'%' '%'+#{cityName}+'%' concat('%',#{cityName},'%')
AND a.iszbg = ${iszbg}
AND a.city_id in (${sqlMap.citys})
GROUP BY a.acca_user_id
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT ,ec.course AS "course" FROM tbl_acca_user a LEFT JOIN tbl_exam_signup es ON a.acca_user_id = es.acca_user_id AND es.exam_signup_status=30 AND es.exam_end_time > #{date} LEFT JOIN tbl_exam_course ec ON es.exam_course_id = ec.exam_course_id WHERE a.acca_user_id IN
#{item.accaUserId}
ORDER BY a.acca_user_id DESC,ec.exam_course_id ASC

select

from tbl_acca_user a where del_flag = '0' AND acca_user_id in

	#{accaUserId}

SELECT 

FROM tbl_acca_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tbl_acca_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE tbl_acca_user SET 
	user_status=#{userStatus}
WHERE acca_user_id=#{accaUserId}

UPDATE tbl_acca_user SET 
	iszbg=#{iszbg}
WHERE acca_user_id=#{accaUserId}

UPDATE tbl_acca_user SET 
	type=#{type}
WHERE acca_user_id=#{accaUserId}

UPDATE tbl_acca_user SET
update_date = now(),
register_name = #{registerName},
register_card_number = #{registerCardNumber},
acca_register_name = #{accaRegisterName}
WHERE acca_user_id = #{accaUserId}
